        //             if is_neighbor_result.value {
        //                 let can_move_to = game.map.can_move_to(x, y);
        //                 if can_move_to.success & can_move_to.value {
        //                     result = actor.set_pos(x, y);
        //                     //result = true;
        //                 }
        //             }
        //         }
        //     }
        // }
