struct Test {
    a: u8,
    b: u8,
}

impl Test {
    fn set_a(&mut self, a: u8) {
        self.a = a;
    }
    fn set_b(&mut self, b: u8) {
        self.b = b;
    }
    fn new(a: u8, b: u8) -> Test {
        Test { a: a, b: b }
    }
}

fn t(t: &mut Test, a: pub u8, b: pub u8) {
    //let t = Test { a: &mut orig_a, b: &mut orig_b };

    t.set_a(a);
    t.set_b(b);

    assert(t.a == a);
    assert(t.b == b);
}

